import React, { useState } from 'react';
import { Button, TextField, Input } from '@material-ui/core'

import { validateEmail } from '../../utils/helpers';

function ContactForm() {
    const [formState, setFormState] = useState({ name: '', email: '', message: '' });

    const [errorMessage, setErrorMessage] = useState('');

    const { name, email, message } = formState;

    const handleSubmit = (event) => {
        event.preventDefault();
        if (!errorMessage) {
            setFormState({ [event.target.name]: event.target.value });
            console.log('Form', formState);
        }
    };

    const handleChange = (event) => {
        if (event.target.name === 'email') {
            const isValid = validateEmail(event.target.value);
            if (!isValid) {
                setErrorMessage('Please enter a valid email address.');
            } else {
                setErrorMessage('');
            }
        } else {
            if (!event.target.value.length) {
                setErrorMessage(`${event.target.name} is required.`);
            } else {
                setErrorMessage('');
            }
        }
    };

    return (
        <section>
            <h1>
                Contact Me
            </h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <TextField classes={{ label: padded}} id="standard-basic" name="name" label="Name" defaultValue={name} onBlur={handleChange} />
                </div>
                <div>
                    <TextField className="padded" id="standard-basic" name="email" label="Email" defaultValue={email} onBlur={handleChange} />
                </div>
                <div>
                    <TextField className="padded" id="standard-basic" name="message" label="Message" multiline rows={4} defaultValue={message} onBlur={handleChange} />
                </div>
                {errorMessage && (
                    <div>
                        <p className="padded error-text">{errorMessage}</p>
                    </div>
                )}
                <Button className="padded" variant="outlined" color="primary" type="submit">Submit</Button>
            </form>
        </section>
    );
}

export default ContactForm;
