import React from 'react';
import profilePhoto from '../../assets/images/avatar.jpeg'

function About() {
    return (
        <section id='about'>
            <h1>About Me</h1>
            <img
                src={profilePhoto}
                className=""
                style={{ width: 200 }}
                alt="profile photo"
            />
            <p>
                My name is Michelle Gleave (she/her). I reside in Salt Lake City, Utah 
                with my husband Mike and my dog Mulder. 
                I am currently a student at the University of Utah Coding Boot Camp. 
                I have experience with HTML, CSS, VBA for Microsoft Office, Adobe 
                Photoshop and Illustrator, and Javascript. I currently work for The 
                Center for Excellence in Higher Education and as a freelance graphic 
                designer. My passion lies in creating clean, streamlined, and aesthetically 
                pleasing applications.
            </p>
        </section>
    )
}

export default About;
